#include <stdio.h>#include <string.h>int broj_samoglasnika(char str[]) {    int i = 0;    int br = 0;    for (i = 0; i < strlen(str); i++) {        if (str[i] == 'a' || str[i] == 'e' || str[i] == 'i' || str[i] == 'o' || str[i] == 'u') {            br++;        }    }    return br;}int main() {    char recenica[] = "ova recenica ima nekoliko rijeci.";    int duljina = strlen(recenica), i = 0, j = 0;    char rijec[100];    while (i < duljina) {        while (recenica[i] == ' ') {            i++;        }        while (recenica[i] != ' ' && recenica[i] != '\0') {            rijec[j++] = recenica[i];            i++;        }        rijec[j] = '\0';        j = 0;        if (broj_samoglasnika(rijec) % 2 != 0) {            printf("%s\n", rijec);        }    }    return 0;}